import React, { useState } from 'react';
import './Destinations.css';
import VideoModal from './VideoModal';  // Import the new VideoModal component

const destinations = [
  {
    name: 'Taj Mahal',
    description: 'A magnificent symbol of love in Agra, India.',
    image: '/images/taj-mahal.jpg',
    videoUrl: 'https://example.com/video/taj-mahal-360.mp4', // 360 video URL for Taj Mahal
  },
  {
    name: 'Christ the Redeemer',
    description: 'The iconic statue overlooking Rio de Janeiro, Brazil.',
    image: '/images/christ-redeemer.jpg',
    videoUrl: 'https://example.com/video/christ-redeemer-360.mp4', // 360 video URL for Christ the Redeemer
  },
  {
    name: 'Machu Picchu',
    description: 'The mystical Incan city in the Peruvian Andes.',
    image: '/images/machu-picchu.jpg',
    videoUrl: 'https://example.com/video/machu-picchu-360.mp4', // 360 video URL for Machu Picchu
  },
  // Add other destinations similarly
];

const Destinations = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [videoUrl, setVideoUrl] = useState('');

  const openVideoModal = (url) => {
    setVideoUrl(url);
    setIsModalOpen(true); // Open the video modal
  };

  const closeModal = () => {
    setIsModalOpen(false); // Close the video modal
  };

  return (
    <div className="destinations-page">
      <h1 className="page-title">Explore the Wonders</h1>
      <p className="page-description">
        Discover the most fascinating destinations from around the world.
      </p>
      <div className="card-container">
        {destinations.map((dest, index) => (
          <div key={index} className="destination-card">
            <div
              className="card-image"
              style={{ backgroundImage: `url(${dest.image})` }}
              onClick={() => openVideoModal(dest.videoUrl)} // Open video on image click
            ></div>
            <div className="card-content">
              <h2 className="wonder-name">{dest.name}</h2>
              <p className="wonder-description">{dest.description}</p>
            </div>
          </div>
        ))}
      </div>

      {/* Modal for displaying the 360 video */}
      {isModalOpen && <VideoModal videoUrl={videoUrl} onClose={closeModal} />}
    </div>
  );
};

export default Destinations;
