import React, { useState } from 'react';
import './Destinations.css';

const destinations = [
  { name: 'Taj Mahal', description: 'A magnificent symbol of love in Agra, India.', image: '/images/taj-mahal.jpg' },
  { name: 'Christ the Redeemer', description: 'The iconic statue in Rio.', image: '/images/christ-redeemer.jpg' },
  // Add other destinations
];

const Destinations = () => {
  const [videoUrl, setVideoUrl] = useState(null);
  const [showViewer, setShowViewer] = useState(false);

  const handleCardClick = async (dest) => {
    try {
      const res = await fetch(`http://localhost:5000/api/videos/${encodeURIComponent(dest.name)}`);
      const data = await res.json();
      setVideoUrl(data.url);
      setShowViewer(true);
    } catch (error) {
      console.error('Error fetching video:', error);
    }
  };

  const handleClose = () => {
    setShowViewer(false);
    setVideoUrl(null);
  };

  return (
    <div className="destinations-page">
      <h1 className="page-title">Explore the Wonders</h1>
      <div className="card-container">
        {destinations.map((dest, index) => (
          <div
            key={index}
            className="destination-card"
            onClick={() => handleCardClick(dest)}
            style={{ cursor: 'pointer' }}
          >
            <div className="card-image" style={{ backgroundImage: `url(${dest.image})` }}></div>
            <div className="card-content">
              <h2>{dest.name}</h2>
              <p>{dest.description}</p>
            </div>
          </div>
        ))}
      </div>

      {showViewer && videoUrl && (
        <div className="fullscreen-iframe">
          <button className="close-btn" onClick={handleClose}>âœ•</button>
          <iframe
            src={videoUrl}
            title="360 View"
            frameBorder="0"
            allowFullScreen
            style={{ width: '100%', height: '100%' }}
          />
        </div>
      )}
    </div>
  );
};

export default Destinations;
