import React, { useState, useEffect } from 'react';
import './Destinations.css';

const destinations = [
  {
    name: 'Taj Mahal',
    description: 'A magnificent symbol of love in Agra, India.',
    image: '/images/taj-mahal.jpg',
  },
  /* … other destinations … */
];

const Destinations = () => {
  const [showPano, setShowPano] = useState(false);

  useEffect(() => {
    if (!showPano) return;

    // 1. Inject API script if needed
    if (!window.google || !window.google.maps) {
      window.initPano = () => {
        new window.google.maps.StreetViewPanorama(
          document.getElementById('pano'),
          {
            position: { lat: 27.17372715, lng: 78.04223481 },
            pov: { heading: 352.04765094, pitch: 0 },
            visible: true,
          }
        );
      };
      const script = document.createElement('script');
      script.src =
        'https://maps.googleapis.com/maps/api/js?key=YOUR_API_KEY&callback=initPano&v=weekly';
      script.defer = true;
      document.head.appendChild(script);
    } else {
      // 2. Already loaded → instantiate directly
      new window.google.maps.StreetViewPanorama(
        document.getElementById('pano'),
        {
          position: { lat: 27.17372715, lng: 78.04223481 },
          pov: { heading: 352.04765094, pitch: 0 },
          visible: true,
        }
      );
    }
  }, [showPano]);

  return (
    <div className="destinations-page">
      <h1 className="page-title">Explore the Wonders</h1>
      <p className="page-description">
        Discover the most fascinating destinations from around the world.
      </p>
      <div className="card-container">
        {destinations.map((dest, idx) => (
          <div
            key={idx}
            className="destination-card"
            onClick={() => dest.name === 'Taj Mahal' && setShowPano(true)}
            style={{ cursor: dest.name === 'Taj Mahal' ? 'pointer' : 'default' }}
          >
            <div
              className="card-image"
              style={{ backgroundImage: `url(${dest.image})` }}
            ></div>
            <div className="card-content">
              <h2 className="wonder-name">{dest.name}</h2>
              <p className="wonder-description">{dest.description}</p>
            </div>
          </div>
        ))}
      </div>

      {showPano && (
        <div className="fullscreen-container">
          <button className="close-btn" onClick={() => setShowPano(false)}>
            ✕
          </button>
          <div id="pano" className="fullscreen-pano"></div>
        </div>
      )}
    </div>
  );
};

export default Destinations;
