import React, { useState, useEffect, useRef } from 'react';
import PhotoSphereViewer from 'photo-sphere-viewer';
import 'photo-sphere-viewer/dist/photo-sphere-viewer.css';
import './Destinations.css';

const destinations = [
  {
    name: 'Taj Mahal',
    description: 'A magnificent symbol of love in Agra, India.',
    image: '/images/taj-mahal.jpg',
  },
  
];

const Destinations = () => {
  const [showViewer, setShowViewer] = useState(false);
  const [imageUrl, setImageUrl] = useState('');
  const viewerRef = useRef(null);
  const containerRef = useRef(null);

  const handleCardClick = async (dest) => {
    try {
      const response = await fetch(`http://localhost:5000/api/videos/${encodeURIComponent(dest.name)}`);
      if (!response.ok) throw new Error('Image not found');
      const data = await response.json();
      setImageUrl(data.videoUrl); // must be a 360° panorama image
      setShowViewer(true);
    } catch (err) {
      console.error(err);
      alert('Could not load the 360 image.');
    }
  };

  const handleClose = () => {
    setShowViewer(false);
    setImageUrl('');
    if (viewerRef.current) {
      viewerRef.current.destroy();
      viewerRef.current = null;
    }
  };

  useEffect(() => {
    if (showViewer && imageUrl && containerRef.current) {
      viewerRef.current = new PhotoSphereViewer.Viewer({
        container: containerRef.current,
        panorama: imageUrl,
        defaultLat: 0.3,
        navbar: ['zoom', 'move', 'fullscreen'],
      });
    }
  }, [showViewer, imageUrl]);

  return (
    <div className="destinations-page">
      <h1 className="page-title">Explore the Wonders</h1>
      <p className="page-description">
        Discover the most fascinating destinations from around the world.
      </p>

      <div className="card-container">
        {destinations.map((dest, index) => (
          <div
            key={index}
            className="destination-card"
            onClick={() => handleCardClick(dest)}
            style={{ cursor: 'pointer' }}
          >
            <div className="card-image" style={{ backgroundImage: `url(${dest.image})` }}></div>
            <div className="card-content">
              <h2 className="wonder-name">{dest.name}</h2>
              <p className="wonder-description">{dest.description}</p>
            </div>
          </div>
        ))}
      </div>

      {showViewer && (
        <div className="fullscreen-viewer">
          <button className="close-btn" onClick={handleClose}>✕</button>
          <div ref={containerRef} className="viewer-container"></div>
        </div>
      )}
    </div>
  );
};

export default Destinations;
